using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;
using Microsoft.VisualStudio.TestTools.UnitTesting;
using Strings;

namespace StringsTests
{
    [TestClass]
    public class SherlockAndValidStringTests
    {
        [TestMethod]
        public void TestMethod1()
        {
            Assert.AreEqual(false, SherlockAndValidString.CanBeConverted("aabbcd"));
        }

        [TestMethod]
        public void TestMethod2()
        {
            Assert.AreEqual(true, SherlockAndValidString.CanBeConverted("jtqgugmcsxvdwidtcyqpogkdifapuloqykjfxruvfrshcehekoiwbpbrprahwvhliglyxynjotbaswnnnmxbkmcftvsdqajemeul"));
        }

        [TestMethod]
        public void TestMethod3()
        {
            Assert.AreEqual(true, SherlockAndValidString.CanBeConverted(""));
        }


        [TestMethod]
        public void TestMethod4()
        {
            Assert.AreEqual(true, SherlockAndValidString.CanBeConverted("hfchdkkbfifgbgebfaahijchgeeeiagkadjfcbekbdaifchkjfejckbiiihegacfbchdihkgbkbddgaefhkdgccjejjaajgijdkd"));
        }
    }
}
